INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('019', '《电子科技学刊》JEST入选Scopus Q2区期刊', '何雨莲', '2022-06-15 00:00:00', '6月，Elsevier正式公布CiteScore 2021指标，该指标用于评估Scopus数据库收录的同行评议期刊、会议录等出版物的国际影响力。《电子科技学刊》*JEST）自2015年被Scopus收录以来，CiteScore指标持续上升，CiteScore 2021相对CiteScore 2020提升237.5%，排名上升31个百分位，成为Scopus Q2区期刊，CiteScoreTracker 2022指标更达到了4.1。', 'http://localhost:8080/image/news/019.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('023', '清水河校区学生公寓六期硕士公寓项目建设进展顺利', '沈娅娜', '2022-07-11 00:00:00', '6月30日上午，副校长朱宏前往清水河校区学生公寓六期硕士公寓施工现场对项目进度、质量和安全进行综合检查。基建处负责人及工程技术人员，工程施工、监理单位相关负责人参加检查。', 'http://localhost:8080/image/news/023.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('106', '四川省副省长罗强“七一”前夕看望慰问成建波教授', '罗莎', '2022-07-10 00:00:00', '　　6月30日，四川省副省长罗强一行来到电子科大沙河校区，在东二院家属楼亲切看望慰问我校成建波教授，代表省委、省政府向成建波颁发并佩戴“光荣在党50年”纪念章，向他致以亲切问候和崇高敬意。', 'http://localhost:8080/image/news/106.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('123', '电子科技大学召开干部教师会议 宣布校行政领导班子调整决定', '李果', '2022-05-23 00:00:00', '月23日下午，学校在清水河校区召开干部教师会议，宣布教育部党组关于学校领导班子成员任免的决定。校党委书记王亚非主持会议。校长曾勇，校领导班子成员，校党委委员、纪委委员，教师代表等出席会议。', 'http://localhost:8080/image/news/123.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('137', '电子科技大学隆重举行2022年学生表彰大会', '王晓刚', '2022-04-29 00:00:00', '4月28日，电子科技大学2022年学生表彰大会在清水河校区图书馆求实厅隆重举行。2020-2021学年国家奖学金、国家励志奖学金、优秀学生奖学金等获奖者，2021-2022年各类竞赛优秀团队及个人，优秀团学组织及个人，社会实践、志愿服务优秀团队及个人，以及2021年度“成电杰出班级”和“成电杰出学生”获得者受到表彰。', 'http://localhost:8080/image/news/137.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('140', '曾勇校长寄语毕业生：在青春赛道上奋力跑出加速度', '曾勇', '2022-06-22 00:00:00', '月22日，电子科技大学隆重举行2022年学生毕业典礼暨学位授予仪式，4260位本科生、4667位硕士生、354位博士生迎来大学“最后一课”。', 'http://localhost:8080/image/news/140.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('169', '学校举行2021-2022年度“先进本科生党支部”答辩会', '罗莎', '2022-06-14 00:00:00', '6月9日，“青春心向党，喜迎二十大”电子科技大学2021-2022年度“先进本科生党支部”答辩会在清水河校区求实厅举行。经过一年的创建实施、支部自查、学院检查、学校检查，共有19个本科生党支部脱颖而出，各支部围绕支部思想建设、组织建设、作风建设等方面，采用新颖多样的形式对党支部年度的创建成果作汇报展示。', 'http://localhost:8080/image/news/169.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('175', '电子科技大学与桂林电子科技大学签署合作协议', '罗莎', '2022-06-22 00:00:00', '6月15日，电子科技大学与桂林电子科技大学合作协议签约仪式在清水河校区举行。校党委书记王亚非，校长曾勇，校领导胡皓全、孔令讲、罗光春，桂林电子科技大学党委书记唐平秋，校长徐华蕊，校领导陈贵英、周娅、张文涛、潘开林出席签约仪式，双方围绕党建引领、学校治理、加快“双一流”建设、提升教学、科研、人才培养质量及增强服务地方能力等进行了座谈交流。副校长胡皓全主持会议。', 'http://localhost:8080/image/news/175.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('201', '教育部检查组到我校开展2022年高校实验室安全现场检查', '孔红宝', '2022-06-28 00:00:00', '6月24日，教育部2022年度高校实验室安全检查组一行来我校开展实验室安全现场检查，从听取工作汇报、实验室现场检查、查阅安全档案三个环节开展相关工作。', 'http://localhost:8080/image/news/201.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('246', '【新征程 创一流】电子科技大学新增17个一流本科专业建设点', '教务处', '2022-06-14 00:00:00', '近日，教育部办公厅印发《关于公布2021年度国家级和省级一流本科专业建设点名单的通知》，电子科技大学8个专业获批国家级一流本科专业建设点，9个专业获批省级一流本科专业建设点。截至目前，在一流专业“双万计划”建设中，2019-2021年度学校累计获批47个一流本科专业建设点，超过85%的招生专业入选国家级、省级一流本科专业建设点，100%覆盖学校电子信息类、计算机类专业。', 'http://localhost:8080/image/news/246.png');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('391', '饶云江教授团队博士生荣获中国光学工程学会创新论文提名奖', '龚元', '2022-07-22 00:00:00', '近日，第七届“中国光学工程学会创新论文奖”评选结果揭晓，我校信息与通信工程学院龚朝阳博士的学位论文《新型光纤微流激光器及其高性能生化传感研究》（导师：饶云江教授、龚元教授）荣获学会创新论文提名奖。', 'http://localhost:8080/image/news/391.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('458', '学校举行刘盛纲院士科技教育基金2022年度颁奖典礼', '易青颖', '2022-06-16 00:00:00', '6月14日，刘盛纲院士科技教育基金2022年度颁奖典礼在清水河校区举行。副校长胡俊，刘盛纲院士，电子科学与工程学院（示范性微电子学院）党委书记李雪梅、院长樊勇、党委副书记王勋，刘盛纲院士科技教育基金管理委员会成员等出席颁奖仪式。上海理工大学庄松林院士视频致贺词。', 'http://localhost:8080/image/news/458.jpg');
INSERT INTO `news` (`id`, `headline`, `author`, `time`, `content`, `image`) VALUES ('635', '第五届激光和粒子束与材料相互作用国际研讨会举行', '于景侠', '2022-06-16 00:00:00', '第五届激光和粒子束与材料相互作用国际研讨会（2022 International Workshop on Laser and Ion Beams Interactions with Materials， 2022 LIBIM）于6月11日至12日举行。来自中国、美国、英国、澳大利亚、新西兰、新加坡等国家和地区的60多名代表与会，围绕“强激光、离子束与材料相互作用”主题展开深入研讨。本次会议由电子科技大学物理学院、中国工程物理研究院激光聚变研究中心和西南科技大学共同发起并主办，物理学院祖小涛教授和中国工程物理研究院激光聚变研究中心郑万国研究员担任会议主席。', 'http://localhost:8080/image/news/635.png');
